    return (
      <div id="authorization">
        <>
          {this.state.authorized ? this.authorized() : this.unauthorized()}
        </>
        <ul>
          <li>
            client@example.com
          </li>
          <li>
            555.555.5555
          </li>
        </ul>
      </div>
    );
  }
}

ReactDOM.render(
  <Contact />, 
  document.getElementById('app')
);
